// 1. ÏûÖÎ†• Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
const input = items[0].json;

// 2. ÎÇ¥Ïö© Ï∂îÏ∂ú ÏãúÎèÑ (Ïó¨Îü¨ Î≥ÄÏàòÎ™Ö ÌôïÏù∏)
// UpstageÎÇò Discord Î≤ÑÏ†ÑÏóê Îî∞Îùº Î≥ÄÏàòÎ™ÖÏù¥ Îã§Î•º Ïàò ÏûàÏñ¥ ÏàúÏÑúÎåÄÎ°ú Ï∞æÏäµÎãàÎã§.
let rawContent = input.content || input.text || input.output || input.data || '';

// 3. üö® ÌïµÏã¨ ÏàòÏ†ï: ÎßåÏïΩ ÎÇ¥Ïö©Ïù¥ 'Îç©Ïñ¥Î¶¨(Object)'ÎùºÎ©¥ Í∏ÄÏûêÎ°ú Í∞ïÏ†ú Î≥ÄÌôò
if (typeof rawContent === 'object') {
  // ÎßåÏïΩ Îç©Ïñ¥Î¶¨ ÏïàÏóê Îòê 'text'ÎÇò 'content'Í∞Ä ÏûàÎã§Î©¥ Í∑∏Í±∏ Í∫ºÎÉÑ
  if (rawContent.text) {
    rawContent = rawContent.text;
  } else if (rawContent.content) {
    rawContent = rawContent.content;
  } else {
    // Í∑∏Í≤ÉÎèÑ ÏïÑÎãàÎ©¥ Îç©Ïñ¥Î¶¨ Ï†ÑÏ≤¥Î•º Í∏ÄÏûê(String)Î°ú Î≥ÄÌôò
    rawContent = JSON.stringify(rawContent);
  }
}

// 4. Ïà´ÏûêÍ∞Ä Îì§Ïñ¥ÏôÄÎèÑ Í∏ÄÏûêÎ°ú Î≥ÄÌôò
if (typeof rawContent !== 'string') {
  rawContent = String(rawContent);
}

// 5. ÎÇ†Ïßú Í≥ÑÏÇ∞ (Í∏∞Ï°¥ Ïú†ÏßÄ)
const messageTime = new Date(input.timestamp || Date.now());
const currentDate = messageTime.toISOString().slice(0, 10);
const dayOfWeek = messageTime.getDay();
const dayNames = ['Ïùº', 'Ïõî', 'Ìôî', 'Ïàò', 'Î™©', 'Í∏à', 'ÌÜ†'];
const today = dayNames[dayOfWeek];

// 6. ÎÇ¥Ïö©Ïù¥ ÎπÑÏñ¥ÏûàÍ±∞ÎÇò "[object Object]"Í∞Ä Í∑∏ÎåÄÎ°ú ÎÇ®ÏùÄ Í≤ΩÏö∞ Ï≤òÎ¶¨
if (!rawContent || rawContent.trim() === '' || rawContent === '{}') {
  return {
    json: {
      content: '', // Îπà ÎÇ¥Ïö©ÏùÄ AIÍ∞Ä Î¨¥ÏãúÌïòÎèÑÎ°ù
      currentDate: currentDate,
      today: today
    }
  };
}

// 7. ÌÖçÏä§Ìä∏ Ï†ïÎ¶¨ (Î∂àÌïÑÏöîÌïú Í≥µÎ∞± Ï†úÍ±∞)
let normalized = rawContent
  .replace(/\n+/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

return {
  json: {
    content: normalized,
    currentDate: currentDate,
    today: today
  }
};

const response = items[0].json;

// content ÌïÑÎìúÏóêÏÑú JSON Ï∂îÏ∂ú
let content = response.content || '';

// ÎßåÏïΩ markdown ÏΩîÎìúÎ∏îÎ°ùÏóê Í∞êÏã∏Ï†∏ ÏûàÏúºÎ©¥ Ï†úÍ±∞
content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '');

// JSON Í∞ùÏ≤¥ Ï∞æÍ∏∞ (Î≥µÏàò ÏãúÎèÑ)
let parsed;

try {
  // 1Ï∞® ÏãúÎèÑ: Ï†ÑÏ≤¥ contentÍ∞Ä JSON
  parsed = JSON.parse(content);
} catch {
  try {
    // 2Ï∞® ÏãúÎèÑ: { Î∂ÄÌÑ∞ ÎßàÏßÄÎßâ }ÍπåÏßÄ Ï∂îÏ∂ú
    const match = content.match(/\{[\s\S]*\}/);
    if (match) {
      parsed = JSON.parse(match[0]);
    } else {
      throw new Error('No JSON found');
    }
  } catch {
    console.log('Failed to parse:', content);
    return {
      json: {
        items: []
      }
    };
  }
}

// items Ï™ºÍ∞úÍ∏∞
return (parsed.items || []).map(item => {
  return { json: item }
});

